mapper.select:
select t1.id as userId, t1.nickName as name, t1.phone as phone, t1.imgUrl as imgUrl, t3.score as score, t2.role as role from usersAccount t1 
	join userFamilyRelation t2 on (t1.id=t2.userId) 
	left join userScoreDayStat t3 on (t1.id=t3.userId) where familyId=#{familyId} and t3.daystamp=#{daystamp}

select t1.id as userId, t1.nickName as name, t1.phone as phone, t1.imgUrl as imgUrl, t3.score as score, t2.role as realRole from usersAccount t1 
	join userFamilyRelation t2 on (t1.id=t2.userId) 
	left join userScoreDayStat t3 on (t1.id=t3.userId) where familyId=#{familyId} and t3.daystamp=#{daystamp}
