mapper.select:
		select t1.imgUrl as imgUrl, memberNum as num, familyName as name, t1.id as familyId from familyInfo t1 
			join usersAccount t2 on (t1.createUserId=t2.id) where t2.enableStranger=1 and t1.createUserId!=#{userId}
			order by familyId asc
       limit #{startPos}, 10;

 		select t1.id as familyId, imgUrl, familyName as name, t2.role as role from familyInfo t1 
			 join userFamilyRelation t2 on(t1.id=t2.familyId)
			where t1.createUserId=#{userId} and t2.userId=#{userId}

select t1.id as familyId, imgUrl, familyName as name, t2.role as role from familyInfo t1 
 join userFamilyRelation t2 on(t1.id=t2.familyId)
where t1.id=#{familyId} and t2.userId=#{userId}
