mapper.select:
SELECT *
FROM  familyRelation where (selfFamilyId=#{familyId})
or  (competitorFamilyId=#{familyId} and relationType=2) and daystamp=#{daystamp}

SELECT *
FROM  familyRelation where  selfFamilyId=#{selfFamilyId}
and competitorFamilyId=#{competitorFamilyId} and relationType=1 and daystamp=#{daystamp}

mapper.update:
update
      familyRelation
       set relationType=2 where id=#{id}

mapper.select:
SELECT *
FROM familyInfo AS t1 JOIN (SELECT ROUND(RAND() * (
(SELECT MAX(id) FROM familyInfo where familyType>=${familyType})-(SELECT MIN(id) FROM familyInfo where familyType>=${familyType})
)+(SELECT MIN(id) FROM familyInfo where familyType>=${familyType})) AS id) AS t2
WHERE t1.id >= t2.id and t1.id!=${id}
ORDER BY t1.id LIMIT 1;
