mapper.update:
       update  interactTempCar 
	   set   lockBatchId=#{lockBatchId}, stealerId=#{stealerId},a.lockStatus=-1,
      lockTime=#{lockTime}  where  id=${id}

mapper.select:
select *
 		FROM  interactTempCar  where  userId=${userId} and daystamp=#{daystamp}
 		and lockStatus=1 and type=1
